import React, { Component, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux'

const UserInfo = () => {

  const username = useSelector(state => state.user.username)
  const followCount = useSelector(state => state.user.followCount)
  const is_followed = useSelector(state => state.user.is_followed)
  const dispatch = useDispatch();

  const btn = () => {
    if (is_followed) {
      return "followed"
    } else {
      return "+follow"
    }
  }

  const handleClick = () => {
    if (is_followed) {
      dispatch({
        type: "updateFollow",
        value: 1,
        is_followed: !is_followed
      })
    } else {
      dispatch({
        type: "updateFollow",
        value: -1,
        is_followed: !is_followed
      })
    }
  }

  let btn1 = btn()

  return ( 
    <div className="card">
      <div className="card-body">
        <div className="row">
          <div className="col-3">
            <img className="img-fluid" style={{borderRadius: "50%"}} src="https://cdn.acwing.com/media/user/profile/photo/85481_lg_140f816091.jpg" alt="" />
          </div>
          <div className="col-9">
            <div className="username" style={{fontWeight: "bold"}}>{username}</div>
            <div className="fans" style={{fontSize: "12px", color: "gray"}}>fans: {followCount}</div>
            <button onClick={handleClick} className="btn btn-secondary btn-sm" style={{border: "0px", padding: "2px 4px", fontSize: "12px"}}>{btn1}</button>
          </div>
        </div>
      </div>
    </div>

  );
}
 
export default UserInfo
