import actions from './action';

const reducer = (state = {
  user: {
    username: "",
    id: 1,
    photo: "",
    followCount: 0,
    access: "",
    refresh: "",
    is_login: false,
  }
}, action) => {
  switch(action.type) {
    case actions.UPDATEACCESS:
      return {
        ...state,
        user: {
          username:"dsfas"
        }
      }
    case actions.UPDATEUSER:
      return {
        ...state,
        user: {
          username: user.username,
          id: user.id,
          photo: user.photo,
          followCount: user.followCount,
          access: user.access,
          refresh: user.refresh,
          is_login: user.is_login,
        }
      }
    default:
      return state;
  }
}

export default reducer
