import React, { Component, useState } from 'react';
import Base from './base';
import $ from 'jquery'

const Friends = () => {

  const [users, setusers] = useState([]);
  const [done, setdone] = useState(false)

  let getuserlist = () => {
    if (!done) {
      $.ajax({
        url: "https://app165.acapp.acwing.com.cn/myspace/userlist/",
        type: "GET",
        success(resp) {
          setusers(resp)
          setdone(true)
        }
      })
    }
  }
  
  getuserlist();


  return ( 
    <Base>
      {users.map(user => (
        <div  className="card hover" key={user.id} style={{marginBottom: "10px", cursor: "pointer"}}>
          <div className="card-body">
            <div className="row">
              <div className="col-1">
                <img className="img-fluid" src={user.photo} alt="" />
              </div>
              <div className="col-11">
                <div className="username" style={{fontWeight:"bold"}}>{user.username}</div>
                <div className="folloerCount" style={{fontSize: "12px", color:"gray"}}>fans: {user.followerCount}</div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </Base>
  );
}

export default Friends
