import React, { Component, useState } from 'react';
import Base from './base';
import UserInfo from './userdynamics/userinfo';
import UserPost from './userdynamics/userpost';
import UserWrite from './userdynamics/userwrite';

const Userdynamics = () => {
  
  const [postCount, setpostCount] = useState(3)
  const [posts, setposts] = useState([
    {
      id: 1,
      userid: 1,
      content: 'a happy day',
    },
    {
      id: 2,
      userid: 2,
      content: 'a happy day',
    },
    {
      id: 3,
      userid: 3,
      content: 'a happy day',
    },
  ])

  const handleClick = (content) => {
    setpostCount(postCount + 1);
    console.log(postCount)
    let post = {
      id: postCount,
      userid: 1,
      content: content,
    }
    setposts([...posts, post]);
  }

  return ( 
    <Base>
      <div className="row">
        <div className="col-3">
          <UserInfo />
          <UserWrite posts={posts} handleClick={handleClick} postCount={postCount} />
        </div>
        <div className="col-9">
          <UserPost posts={posts} />
        </div>
      </div>
    </Base>
  );
}

export default Userdynamics
