import React, { Component, useState } from 'react';
import Base from './base';
import $ from 'jquery'
import { Link } from 'react-router-dom';

const Friends = () => {

  const [users, setusers] = useState([]);
  const [done, setdone] = useState(false)

  const getuserlist = () => {
    if (!done) {
      $.ajax({
        url: "https://app165.acapp.acwing.com.cn/myspace/userlist/",
        type: "GET",
        success(resp) {
          setusers(resp)
          setdone(true)
        }
      })
    }
  }

  getuserlist();

  return ( 
    <Base>
      {users.map(user => (
        <Link className="nav-link active" to="/myspace/userdynamics/{user.id}" key={user.id}>
          <div className="card hover" style={{marginBottom: "10px", cursor: "pointer"}}>
            <div className="card-body">
              <div className="row">
                <div className="col-1">
                  <img className="img-fluid" src={user.photo} alt="" />
                </div>
                <div className="col-11">
                  <div className="username" style={{fontWeight:"bold"}}>{user.username}</div>
                  <div className="folloerCount" style={{fontSize: "12px", color:"gray"}}>fans: {user.followerCount}</div>
                </div>
              </div>
            </div>
          </div>
        </Link>
      ))}
    </Base>
  );
}

export default Friends
